/*jshint unused:false */
/*global tinymce:true */

/**
 * Example plugin that adds a toolbar button and menu item.
 */
tinymce.PluginManager.add('form', function(editor, url) {
	// Add a button that opens a window
	editor.addButton('form', {
		text: 'My Form',
		icon: false,
		onclick: function() {
			// Open window
			editor.windowManager.open({
				title: 'TinyMCE site',
				url: url + '/dialog.html',
				width: 600,
				height: 400,
				onsubmit: function() {
							// Top most window object
							var win = editor.windowManager.getWindows()[0];

							// Insert the contents of the dialog.html textarea into the editor
									
				editor.insertContent(
				win.getContentWindow().document.getElementById('content').value);
				
							// Close the window
							win.close();
						},
				buttons: [
					{
						text: 'Insert',
						onclick: function() {
							// Top most window object
							var win = editor.windowManager.getWindows()[0];

							// Insert the contents of the dialog.html textarea into the editor
									
				editor.insertContent(
				win.getContentWindow().document.getElementById('content').value);
				
							// Close the window
							win.close();
						}
					},

					{text: 'Close', onclick: 'close'}
				]
			});

		}
	});

	// Adds a menu item to the tools menu
	editor.addMenuItem('myform', {
		text: 'Form Items',
		context: 'tools',
		onclick: function() {
			// Open window with a specific url
			editor.windowManager.open({
				title: 'TinyMCE site',
				url: url + '/dialog.html',
				width: 600,
				height: 400,
				buttons: [
					{
						text: 'Insert',
						onclick: function() {
							// Top most window object
							var win = editor.windowManager.getWindows()[0];

							// Insert the contents of the dialog.html textarea into the editor
							editor.insertContent(win.getContentWindow().document.getElementById('content').value);

							// Close the window
							win.close();
						}
					},

					{text: 'Close', onclick: 'close'}
				]
			});
		}
	});
});